import React, { useState } from 'react';
import axios from 'axios';

const API = 'http://localhost:5000/api/appointments'; 

export default function AppointmentForm({ onCreated }) {
  const [form, setForm] = useState({
    patientName: '',
    date: '',
    time: '',
    reason: ''
  });
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');

  const onChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    setError('');
    try {
      await axios.post(API, form);
      // notify parent (App.js) so it can re-fetch and update the table
      await onCreated?.();
      // clear form
      setForm({ patientName: '', date: '', time: '', reason: '' });
    } catch (err) {
      setError(err?.response?.data?.error || 'Failed to create appointment');
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: 'grid', gap: 8, gridTemplateColumns: '1fr 1fr 1fr 1fr auto', marginBottom: 16 }}>
      <input name="patientName" placeholder="Patient Name" value={form.patientName} onChange={onChange} required />
      <input name="date" type="date" value={form.date} onChange={onChange} required />
      <input name="time" type="time" value={form.time} onChange={onChange} required />
      <input name="reason" placeholder="Reason" value={form.reason} onChange={onChange} />
      <button type="submit" disabled={submitting}>{submitting ? 'Saving…' : 'Add'}</button>
      {error && <div style={{ color: 'red', gridColumn: '1 / -1' }}>{error}</div>}
    </form>
  );
}
