import React, { useState } from 'react';
import axios from 'axios';

const AppointmentForm = () => {
  const [patientName, setPatientName] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [reason, setReason] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post('http://localhost:5000/api/appointments', {
      patientName, date, time, reason
    });
    setPatientName('');
    setDate('');
    setTime('');
    setReason('');
    alert('Appointment created!');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input placeholder="Patient Name" value={patientName} onChange={e => setPatientName(e.target.value)} required />
      <input type="date" value={date} onChange={e => setDate(e.target.value)} required />
      <input type="time" value={time} onChange={e => setTime(e.target.value)} required />
      <textarea placeholder="Reason" value={reason} onChange={e => setReason(e.target.value)} required />
      <button type="submit">Add Appointment</button>
    </form>
  );
};

export default AppointmentForm;
